import Foundation

class AllProductScreenInteractor {
    
    // MARK: Delegate initialization
    var presenter: AllProductScreenInterectorToPresenterProtocol?
    
    // MARK: - Call Service
    func getProductsAPICall(_ data: ProductsRequestModel) {
        presenter?.fetchingStart()
        APIClient.getProducts(productsRequestModel: ProductsRequestModel(page: data.page, size: data.size)) { result in
            switch result {
            case .success(let response):
                self.presenter?.success(response)
            case .failure(let error):
                self.presenter?.fail(error: error)
            }
        }
    }
    
}

// MARK: - Presenter to Interactor
extension AllProductScreenInteractor: AllProductScreenPresentorToInterectorProtocol {
    
}
